class Solution {
public:
    const int mod = 1e9 + 7;
    int f[305][405], re[305];
    inline int add(int x, int y){
        return x + y >= mod ? x + y - mod : x + y;
    }
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        for(int i = 1; i <= n; ++i) re[i] = -1;
        for(int i = 0; i < requirements.size(); ++i) re[requirements[i][0] + 1] = requirements[i][1];
        if(re[1] != -1 && re[1] != 0) return 0;
        f[1][0] = 1;
        for(int i = 2; i <= n; ++i){
            for(int j = 0; j <= 400; ++j){
                for(int k = 1; k <= i; ++k)
                    if(j >= i - k)
                        f[i][j] = add(f[i][j], f[i - 1][j - (i - k)]);
            }
            for(int j = 0; j <= 400; ++j)
                if(re[i] != -1 && re[i] != j)
                    f[i][j] = 0;
        }
        return f[n][re[n]];
    }
};
