class Solution {
public:
    const int MOD = 1e9 + 7;

    int numberOfPermutations(int n, vector<vector<int>>& requirements) {

        vector<vector<int>> dp(n+1, vector<int>(401, 0));
        dp[0][0] = 1;

        vector<int> req(n, -1);

        for(auto &r : requirements) 
            req[r[0]] = r[1];

        for(int i=1;i<=n;i++) {
            vector<int> pre(402, 0);
            for(int j=0;j<=400;j++) 
                pre[j + 1] = (pre[j] + dp[i - 1][j]) % MOD;

            for(int j=0;j<=400;j++) {
                if(req[i - 1] != -1 && j != req[i - 1])
                    continue;
                dp[i][j] = (pre[j + 1] - pre[max(0, j - i + 1)] + MOD) % MOD;
            }
        }

        int result = 0;
        for (int j=0;j<=400;j++) 
            result = (result + dp[n][j]) % MOD;

      return result;
    }
};
