class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& b) {
        const long long int MOD = 1e9 + 7;

        vector<int> a(n, -1);
        for (auto i: b) {
            a[i[0]] = i[1];
        }

        if (a[0] != -1 && a[0] != 0) return 0;

        int m = 405;
        vector<vector<long long int>> dp(n, vector<long long int>(m, 0));
        dp[0][0] = 1;
        for (int i = 1; i < n; i++) {
            for (int j = 0; j <= i; j++) {
                for (int k = 0; k + j < m; k++) {
                    dp[i][k + j] += dp[i - 1][k];
                    dp[i][k + j] %= MOD;
                }
            }
            if (a[i] == -1) continue;

            for (int j = 0; j < m; j++) {
                if (a[i] != j) dp[i][j] = 0;
            }
        }
        return dp[n - 1][a[n - 1]];
    }
};
