class Solution {
public:
    typedef long long ll;
    int numberOfPermutations(int n, vector<vector<int>>& r) {
     sort(r.begin(),r.end());
        ll cnt=r[r.size()-1][1];
            vector<ll>dp(cnt+1,0);
        ll p=1e9+7;
        dp[0]=1;
        ll op=r.size()-1;

        for(ll i=n-1;i>=0;i--){
         vector<ll>dp2(cnt+1,0);
            for(ll j=0;j<=i;j++){
               for(ll k=0;k<=cnt;k++){
                    if(op>=0&&r[op][0]==i){
                        if(k==cnt-r[op][1]){
                            if( k+j<=cnt){
                                       dp2[k+j]=((dp2[k+j]%p)+(dp[k]%p))%p;
                            }

                        } 
                    }
                   else{
                        if(k+j<=cnt){
                                 dp2[k+j]=((dp2[k+j]%p)+(dp[k]%p))%p;
                        }
                   }
               }
            }
            for(ll j=0;j<=cnt;j++){
                       dp[j]=dp2[j];
            }
             if(op>=0&&r[op][0]==i){
                op--;
             }

        }
        return dp[cnt];
    }
};
