class Solution {
public:
    int mod = 1e9+7;

    void pre(int ind,vector<vector<long long>> &dp){
        for(int i = 1;i<dp[ind].size();i++){
            dp[ind][i]+=dp[ind][i-1];
            dp[ind][i]%=mod;
        }

        return;
    }
    int numberOfPermutations(int n, vector<vector<int>>& R) {

        vector<vector<long long>> dp(n+1,vector<long long> (405,0));
        dp[0][0] = 1;
        sort(R.begin(),R.end());

        if(R[0][0]==0&&R[0][1]!=0) return 0;

        int P = 0;
        if(R[0][0]==0) P++;


        // for(auto &val:dp[0]){
        //         cout<<val<<" ";
        //     }
        //     cout<<endl;

        pre(0,dp);

        for(int i = 1;i<n;i++){

            for(int j = 0;j<dp[i].size();j++){
                long long ind = j - i - 1,L = 0,R = dp[i-1][j];
                if(ind>=0){
                    L = dp[i-1][ind];
                }
                R = R - L + mod;
                R%=mod;
                dp[i][j] = R;
            }


            if(P<R.size()&&R[P][0]==i){

                for(int j = 0;j<dp[i].size();j++){
                    if(j!=R[P][1]){
                        dp[i][j] = 0;
                    }
                }
                P++;

            }

            // for(auto &val:dp[i]){
            //     cout<<val<<" ";
            // }
            // cout<<endl;
            pre(i,dp);

        }

        // cout<<endl;



        return dp[n-1][dp[0].size()-1];

    }
};
