const long long int M=(int)1e9+7;
class Solution {
public:
    long long calDp(int ind,int cnt,vector<int>& req,vector<vector<long long>>& dp){
        if(req[ind]!=-1&&cnt!=req[ind])return 0;
        else if(ind==0){
            if(req[ind]>0 || cnt>0)return 0;
            else return 1;
        }else if(dp[ind][cnt]!=-1)return dp[ind][cnt];
        else{
            long long val=0;
            for(int i=0;i<=min(ind,cnt);++i){
                val+=calDp(ind-1,cnt-i,req,dp); if(val>=M)val-=M;
            }
            return (dp[ind][cnt]=val);
        }
    }
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        vector<int> req(n,-1);
        int maxi=0; for(auto &e:requirements){maxi=max(maxi,e[1]);  req[e[0]]=e[1];}
        vector<vector<long long>> dp(n,vector<long long>(maxi+1,-1));
        return calDp(n-1,req[n-1],req,dp);
    }
};
