#define MOD 1000000007
#define INF 1e18
#define endl "\n"
#define pb push_back
#define ppb pop_back
#define ff first
#define ss second
#define sz(x) ((int)(x).size())
#define all(x) (x).begin(), (x).end()

class Solution {
public:
    Solution() {
        ios::sync_with_stdio(false); cin.tie(0);
    }

    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        vector<int> req(n + 1, -1);
        for(auto &requirement: requirements) {
            int end = requirement[0], cnt = requirement[1];
            req[end + 1] = cnt;
        }

        int k = req.back();
        vector<vector<int>> dp(n + 1, vector<int>(k + 1));
        dp[0][0] = 1;

        for(int i = 1; i <= n; i++) {
            int l = 0, r = k;
            if(req[i] != -1)
                l = req[i], r = req[i];
            for(int j = l; j <= r; j++) {
                for(int inv = 0; inv <= min(i - 1, j); inv++) {
                    if(j - inv < 0)
                        continue;
                    dp[i][j] = (dp[i][j] + 0ll + dp[i - 1][j - inv]) % MOD;
                }
            }
        }

        return dp[n][k];
    }
};
