#define lli long long
lli mod=1e9+7;
class Solution {
public:
    lli find(lli n,lli k,vector<vector<lli>>&dp,vector<lli>&v,vector<lli>&ma,vector<lli>&mi){
        //cout<<n<<" "<<k<<" "<<v[n]<<" "<<ma[n]<<" "<<mi[n]<<endl;
    if(v[n]!=-1){
        if(k!=v[n]){
            return dp[n][k]=0;
        }
    }
        //cout<<"hi\n";
        if(k>ma[n]||k<mi[n]){
            return dp[n][k]=0;
        }
        //cout<<"hlo\n";
    if(k==0){
        return 1;
    }
    if(n==0){
        return 0;
    }
    if(dp[n][k]!=-1){
        return dp[n][k]%mod;
    }
    lli ans=0;
    for(lli i=0;i<=min(n-1,k);i++){
        ans+=find(n-1,k-i,dp,v,ma,mi)%mod;
        ans=ans%mod;
    }
        //cout<<n<<" "<<k<<" "<<ans<<endl;
    return dp[n][k]=ans;
}
    int numberOfPermutations(int n, vector<vector<int>>&vp) {
        vector<vector<lli>>dp(301,vector<lli>(401,-1));
        sort(vp.begin(),vp.end());
        vector<lli>v(n+1,-1);
        for(lli i=0;i<vp.size();i++){
            v[vp[i][0]+1]=vp[i][1];
        }
        lli t=0;
        vector<lli>ma(n+1),mi(n+1);
        for(lli i=0;i<=n;i++){
            if(v[i]!=-1){
                t=v[i];
            }
            mi[i]=t;
        }
        t=1e18;
        for(lli i=n;i>=0;i--){
            if(v[i]!=-1){
                t=v[i];
            }
            ma[i]=t;
            //cout<<i<<" "<<ma[i]<<" "<<mi[i]<<endl;
        }
        t=find(n,v[n],dp,v,ma,mi)%mod;
        return (int)(t);
    }
};
