from typing import List

MOD = 1_000_000_007
MAX_INVERSIONS = 400

class Solution:
    def numberOfPermutations(self, n: int, requirements: List[List[int]]) -> int:
        req_map = {req[0] + 1: req[1] for req in requirements}

        perm_counts = [[0] * (MAX_INVERSIONS + 1) for _ in range(n + 1)]
        perm_counts[0][0] = 1

        for length in range(1, n + 1):
            for inv in range(MAX_INVERSIONS + 1):
                for new_pos in range(length):
                    prev_inv = inv - new_pos
                    if prev_inv >= 0:
                        perm_counts[length][inv] = (perm_counts[length][inv] + perm_counts[length - 1][prev_inv]) % MOD

            if length in req_map:
                target_inv = req_map[length]
                for inv in range(MAX_INVERSIONS + 1):
                    if inv != target_inv:
                        perm_counts[length][inv] = 0

        result = sum(perm_counts[n]) % MOD

        return result
