class Solution {
    const int MOD = 1e9 + 7;
public:
    int mx;
    int solve(int pos, int cnt, vector<int> &req, vector<vector<int>> &dp) {
        // cout << pos << " " << cnt << endl;
        if(pos == -1 && cnt == 0) return 1;
        if(pos == -1) return 0;
        if(cnt < 0 || (req[pos] != -1 && req[pos] != cnt)) return 0;

        auto &res = dp[pos][cnt];
        if(res != -1) return res;

        res = 0;
        // cout << "START: " << pos << " " << cnt << endl;
        for (int i = 0; i <= pos; i++) {
            auto ncnt = cnt - i;

            res = (res + solve(pos - 1, ncnt, req, dp)) % MOD;
        }

        // cout << "DEP: " << pos << " " << cnt << " " << res << endl;

        return res;
    }

    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        auto req = vector(n, -1);
        mx = 0;

        for (auto &x : requirements) req[x[0]] = x[1], mx = max(mx, x[1]);

        auto dp = vector(n, vector(mx + 5, -1));

        auto res = solve(n - 1, req[n - 1], req, dp);

        return res;
    }
};
