MOD = 10**9+7

class Solution:
    def numberOfPermutations(self, n: int, req: List[List[int]]) -> int:


        req.sort(key=lambda x: x[0])
        m = [-2]*405
        for a,b in req:
            m[a] = b
        dp = [[-2]*405 for _ in range(405)]



        def f(n,k):
            if n==0:
                return 0
            if m[n-1] != -2 and m[n-1] != k:
                return 0
            if k==0 and n == 1: 
                return 1
            if dp[n][k] == -2:
                ans = 0
                for i in range(min(n,k+1)):
                    ans += f(n-1,k-i)
                dp[n][k] = ans % MOD
            return dp[n][k]
