const int mod = 1e9 + 7;
class Solution
{
public:
    vi t;
    vector<vvi> dp;
    int f(int i, int p, int n)
    {
        if (i == n)
            return 1;
        if (dp[i][p][t[i] + 1] != -1)
            return dp[i][p][t[i] + 1];
        int ans = 0;
        if (t[i] != -1)
        {
            for (int j = 0; j <= i; j++)
            {
                if (p + i - j == t[i])
                    ans = (ans + f(i + 1, p + i - j, n)) % mod;
            }
        }
        else
        {
            for (int j = 0; j <= i; j++)
            {
                if (p + i - j <= 400)
                    ans = (ans + f(i + 1, p + i - j, n)) % mod;
            }
        }
        return dp[i][p][t[i] + 1] = ans;
    }

    int numberOfPermutations(int n, vector<vector<int>> &requirements)
    {
        t.resize(n, -1);
        for (auto &r : requirements)
            t[r[0]] = r[1];
        dp.resize(n + 1, vector<vector<int>>(401, vector<int>(402, -1)));
        return f(0, 0, n);
    }
};
