class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& r) {
        int mod=1e9+7;
        vector<int>ma(n,-1);
        for(int i=0;i<r.size();i++) ma[r[i][0]]=r[i][1];
        vector<vector<int>>dp(n+1,vector<int>(401,0));
        dp[0][0]=1;
        if(ma[0]>0) return 0;
        for(int i=1;i<n;i++){
            if(ma[i]!=-1){
                for(int j=0;j<=i;j++){
                    if(ma[i]-i+j<0) continue;
                    dp[i][ma[i]]+=dp[i-1][ma[i]-(i-j)];
                    if(dp[i][ma[i]]>=mod) dp[i][ma[i]]-=mod;
                }
            }
            else{
                for(int j=0;j<401;j++){
                    for(int k=0;k<=i;k++){
                        if(j-i+k<0) continue;
                        dp[i][j]+=dp[i-1][j-(i-k)];
                        if(dp[i][j]>=mod) dp[i][j]-=mod;
                    }
                }
            }
        }
        int ans=0;
        for(int j=0;j<=400;j++){
            ans+=dp[n-1][j];
            if(ans>=mod) ans-=mod;
        }
        return ans;
    }
};
