class Solution {
public:
    vector<vector<int>> dp;
    const int mod = 1e9+7;
    int siz;
    int solve(int i,int j,int &u,vector<vector<int>> &requirements){
        if(i==siz)
            return 1;
        if(dp[i][j]!=-1)
            return dp[i][j];
        int ans = 0;
        if(u==requirements.size() || requirements[u][0]!=i){
        for(int k = 0;k<=min(i,j);k++){
            ans = (ans+solve(i+1,j-k,u,requirements))%mod;
        }
        }
        else{
            if(j>i)
                return dp[i][j] = 0;
            u++;
            if(u<requirements.size())
            ans = (ans+solve(i+1,requirements[u][1],u,requirements))%mod;
            else
            ans = (ans+solve(i+1,siz,u,requirements))%mod;
            u--;
        }

        return dp[i][j] = ans;
    }
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        sort(requirements.begin(),requirements.end());
        vector<vector<int>> v;
        for(int i = 0;i<requirements.size();i++){
            if(v.empty())
                v.push_back(requirements[i]);
            else if(requirements[i][1]-requirements[i-1][1]>=0)
                v.push_back({requirements[i][0],requirements[i][1]-requirements[i-1][1]});
            else
                return 0;
        }
        requirements = v;
        int u = 0;
        siz = n;
        dp.resize(301,vector<int> (401,-1));
        return solve(0,requirements[u][1],u,requirements);

    }
};
