#include <iostream>
#include <vector>

using namespace std;
const int MOD = 1e9 + 7;
const int MAX_INV = 400;
class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {

        vector<vector<int>> dpp(n + 1, vector<int>(MAX_INV + 1, 0));
        dpp[0][0] = 1;
        vector<int> reqInv(n, -1);
        for (const auto& req : requirements) {
            reqInv[req[0]] = req[1];
        }


        solve(n, MAX_INV, dpp, reqInv);
        int ans=dpp[n][reqInv[n-1]];

        return ans;
    }

private:
    void solve(int n, int MAX_INV, vector<vector<int>>& dpp, vector<int>& reqInv) {
        for (int len = 1; len <= n; ++len) {
            for (int inv = 0; inv <= MAX_INV; ++inv) {
                for (int k = 0; k < len; ++k) {
                    if (inv >= k) {
                        dpp[len][inv] = (dpp[len][inv] + dpp[len - 1][inv - k]) % MOD;
                    }
                }
            }
            if (reqInv[len - 1] != -1) {
                int requiredInv = reqInv[len - 1];
                for (int inv = 0; inv <= MAX_INV; ++inv) {
                    if (inv != requiredInv) {
                        dpp[len][inv] = 0;
                    }
                }
            }
        }
    }
};
