class Solution {
public:
    int dp[301][401];
    int rec(int i,vector<int>&p,int n,int cur){
        // cout<<i<<" "<<rem<<endl;
        if(i==n-1){
            if(cur+n-1>=p[n-1] && cur<=p[n-1]) return 1;
            else return 0;
        }
        if(dp[i][cur]!=-1) return dp[i][cur];
        int ans = 0;
        if(p[i]==-1){
            for(int j=i; j>=0; j--){
            if(cur+j<=400) ans+=rec(i+1,p,n,cur+j);
            ans%=1000000007;
        }
        }
        else{
            int maxi = i;
            if(p[i]>maxi+cur || p[i]<cur) ans=0;
            else ans = rec(i+1,p,n,p[i]);
        }

        return dp[i][cur]=ans;

    }
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        vector<int>p(n,-1);
        for(auto c:requirements){
            p[c[0]]=c[1];
        }
        // int x = 0;
        // for(int i=0; i<n; i++){
        //     if(p[i]!=-1){
        //         p[i]-=x;
        //         x+=p[i];
        //     }
        // }
        // reverse(p.begin(),p.end());
        // for(auto c:p) cout<<c<<" ";
        // cout<<endl;
        memset(dp,-1,sizeof(dp));
        return rec(0,p,n,0);
    }
};
