class Solution {
public:
    using LL = long long;
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        sort(requirements.begin(), requirements.end(), [](vector<int> &a, vector<int> &b){
            return a[0] < b[0];
        });
        const LL mod = 1e9 + 7;
        int ind = 1;
        LL curr = 1;
        LL lastcnt = 0;
        LL last = 0;
        for(auto req : requirements) {
            LL end = req[0];
            LL cnt = req[1];
            // cout << "Checking " << end << ' ' << cnt << endl;
            LL dist = end - last;
            vector<vector<LL>> dp(dist + 1, vector<LL>(401, 0));
            dp[0][lastcnt] = curr;
            // cout << "DIST " << dist << endl;
            for(LL i = 0; i < dist; i++, ind++) {
                // cout << "ADDING " << ind << endl;
                for(LL q = 0; q <= 400; q++) {
                    for(LL j = 0; j <= ind; j++) {
                        // cout << j << ' ' << ind << endl;
                        if (j + q <= 400) {
                            dp[i + 1][q + j] = (dp[i + 1][q + j] + dp[i][q]) % mod;
                        }
                    }
                }
                // cout << ind << " : ";
                // for(int q = 0; q < 10; q++) cout << dp[i + 1][q] << ' ';
                // cout << endl;
            }
            // cout << "Z" << endl;
            curr = dp[dist][cnt];
            lastcnt = cnt;
            last = end;
        }
        return curr;
    }
};
