limit=400
class Solution:
    def numberOfPermutations(self, n: int, requirements: List[List[int]]) -> int:
        s={i[0]+1:i[1] for i in requirements}
        c=[[0]*(limit+1) for _ in range(n+1)]
        c[0][0]=1

        for i in range(1,n+1):
            for j in range(limit + 1):
                for k in range(i):
                    p=j-k
                    if p>=0: c[i][j]=(c[i][j]+c[i-1][p])%1000000007
            if i in s:
                tar=s[i]
                for j in range(limit+1):
                    if j!=tar: c[i][j]=0
        sol=sum(c[n])%1000000007
        return sol
