class Solution {
public:
    typedef long long ll;
    ll mod = 1e9+7;
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        sort(requirements.begin(), requirements.end());
        int j = 0;
        int cnt = 0;
        int ans = 1;
        for(auto& x: requirements){
            vector<ll> dp(x[1]+1);
            dp[cnt]=ans;
            while(j<=x[0]){
                vector<ll> dp2(x[1]+1);
                for(int i=0; i<=j; i++){
                    for(int z=0; z<=x[1]-i; z++){
                        dp2[i+z]=(dp[z]+dp2[i+z])%mod;
                    }
                }
                j++;
                swap(dp, dp2);
            }
            cnt=x[1];
            ans=dp[x[1]];
        }
        return(ans);
    }
};
