class Solution {
public:
    const int mod = 1e9 + 7;
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        map<int,int> mp;
        for(auto it:requirements){
            mp[it[0] + 1] = it[1];
        }
        vector<vector<int>> dp(n + 1, vector<int> (401, 0));
        dp[0][0] = 1;

        for(int i = 1; i <= n; i++){
            for(int j = 0; j <= 400; j++){
                for(int k = 0; k < i; k++){
                    int a = j - k;
                    if(a >= 0){
                        dp[i][j] += dp[i - 1][a];
                        dp[i][j] %= mod;
                    }
                }
            }
            if(mp.count(i)){
                for(int j = 0; j <= 400; j++){
                    if(j != mp[i]){
                        dp[i][j] = 0;
                    }
                }
            }
        }
        int ans = 0;
        for(auto it:dp[n]){
            ans += it;
            ans %= mod;
        }
        return ans;
    }
};
