class Solution {
public:
    static const int N=400;
     int mark[N+1];
    static const int mod=1e9+7;
    int dp[N+1][N+1];

    int numberOfPermutations(int n, vector<vector<int>>& v) {
        for(int i=0;i<=N;i++)mark[i]=-1;
        for(auto vv:v)
            mark[vv[0]+1]=vv[1];
        dp[0][0]=1;
        int tot=mark[n];
        for(int i=1;i<=n;i++){
            for(int j=0;j<=tot;j++){
                if(mark[i]!=-1 && j!=mark[i])continue;
                for(int k=j;k>=0;k--){
                    if(j-k>=i)break;
                    (dp[i][j]+=dp[i-1][k])%=mod;
                }
            }
        }
//         for(int i=1;i<=n;i++){
//             for(int j=0;j<=tot;j++)cout<<dp[i][j]<<" ";

//         cout<<endl;
        // }

        int ans=dp[n][tot];
        return ans;
    }
};
