class Solution:
    def numberOfPermutations(self, n: int, requirements: List[List[int]]) -> int:
        MOD = 10**9 + 7
        l = 400
        dp = [[0] * (l + 1) for _ in range(n + 1)]
        dp[0][0] = 1
        mp = {req[0] + 1: req[1] for req in requirements}
        for i in range(1, n + 1):
            for j in range(l + 1):
                for k in range(i):
                    pv = j - k
                    if pv >= 0:
                        dp[i][j] = (dp[i][j] + dp[i - 1][pv]) % MOD
            if i in mp:
                for j in range(l + 1):
                    if j != mp[i]:
                        dp[i][j] = 0

        return sum(dp[n]) % MOD
