class Solution {
public:

    const int MOD=1e9+7;
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        int m=n*(n-1)/2;
        vector<vector<int>>dp(n+1,vector<int>(m+1,0));
        vector<int>invReq(n,-1);
        for(auto& req:requirements) {
            invReq[req[0]]=req[1];
        }
        dp[0][0] = 1;
        for(int i=1;i<=n;++i) {
            vector<int>prefix_sum(m+1,0);
            for(int j=0;j<=m;++j) {
                prefix_sum[j]=(j==0?dp[i-1][j]:(prefix_sum[j-1]+dp[i-1][j])%MOD);
            }
            for(int j=0;j<=m;++j){
                if(j<i){
                    dp[i][j]=prefix_sum[j];
                }else{
                    dp[i][j]=(prefix_sum[j]-prefix_sum[j-i]+MOD)%MOD;
                }
            }
            if(invReq[i-1]!=-1){
                for(int j=0;j<=m;++j){
                    if(j!=invReq[i-1]){
                        dp[i][j]=0;
                    }
                }
            }
        }
        int ans=0;
        for(int j=0;j<=m;++j) {
            ans=(ans+dp[n][j])%MOD;
        }
        return ans;
    }
};
