const int mod = (int)1e9+7;
class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        map<int, int> mp;
        for (auto &x:requirements) mp[x[0]+1] = x[1];

        int ans = 0;
        vector<vector<int>> dp(n + 1,vector<int>(401,0));
        dp[0][0] = 1;

        for (int i = 1; i <= n; i++){
            for (int inv = 0; inv <= 400; ++inv) {
                for (int j=0;j<i; j++) {
                    int pre = inv-j;
                    if (pre >= 0) {
                        dp[i][inv] = (dp[i][inv]+dp[i-1][pre])%mod;
                    }
                }
            }
            auto it = mp.find(i);
            if(it!=mp.end()){
                int temp1 = mp[i];
                for (int inv = 0; inv <= 400;inv++) {
                    if (inv != temp1) dp[i][inv] = 0;
                }
            }
        }
        for (auto &x:dp[n]) {
            ans = (ans+x)%mod;
        }

        return ans;
    }
};
