class Solution:
    def numberOfPermutations(self, n: int, reqs: List[List[int]]) -> int:
        M = 10**9 + 7
        MAX_INV = 400

        req_map = {}
        for r in reqs:
            req_map[r[0] + 1] = r[1]

        pc = [[0] * (MAX_INV + 1) for _ in range(n + 1)]
        pc[0][0] = 1

        for l in range(1, n + 1):
            for i in range(MAX_INV + 1):
                for np in range(l):
                    pi = i - np
                    if pi >= 0:
                        pc[l][i] = (pc[l][i] + pc[l - 1][pi]) % M

            if l in req_map:
                ti = req_map[l]
                for i in range(MAX_INV + 1):
                    if i != ti:
                        pc[l][i] = 0

        res = sum(pc[n]) % M
        return res
