class Solution {
public:


    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        int M=1e9+7;
        int dp[401][n];
        for(int j=0; j<401; j++) for(int k=0; k<n; k++) dp[j][k]=0;
        sort(requirements.begin(),requirements.end());
        int p=0;
        dp[0][0]=1;
        vector<int>psum(401);
        psum[0]=1;
        for(int i=1; i<n+1; i++){
            for(int j=0; j<401; j++){
                for(int k=0; k<=min(i-1,j); k++){
                    dp[j][k]=psum[j-k];
                }
            }
            if(p<requirements.size() && i-1==requirements[p][0]){
                for(int j=0; j<401; j++){
                    for(int k=0; k<=min(i-1,j); k++){
                        if(j!=requirements[p][1]) dp[j][k]=0;
                    }
                }
                p++;
            }
            // for(int j=0; j<3; j++){
            //     for(int k=0; k<3; k++){
            //         cout<<i<<" "<<j<<" "<<k<<" "<<dp[j][k]<<"\n";
            //     }
            // }
            for(int j=0; j<401; j++){
                int cnt=0;
                for(int k=0; k<=min(i-1,j); k++){
                    cnt+=dp[j][k];
                    cnt%=M;
                }
                psum[j]=cnt;
            }
        }
        int ans=0;
        for(int j=0; j<401; j++){
            ans+=psum[j];
            ans%=M;
        }
        return ans;

    }
};
