class Solution {
#define ll long long
ll get(int s, int e, int sum){
    int n = e-s+2;

    vector<vector<ll>> dp(n+1, vector<ll>(sum+1, 0));
    dp[0][0] = 1;
    for(int i=s; i<=e; i++){
        for(int j=0; j<=sum; j++){

            for(int k=0; k<i; k++){

                if(j+k <= sum){
                    dp[i-s+1][j+k] =(dp[i-s+1][j+k] + dp[i-s][j])%1000000007;
                }
            }
        }
    }

    // for(int i=0; i<n; i++){
    //     for(int j=0; j<=sum; j++){
    //         cout << dp[i][j] << " ";
    //     }
    //     cout << '\n';
    // }
    // cout << '\n';
    return dp[n-1][sum] % 1000000007;

}
public:
    int numberOfPermutations(int n, vector<vector<int>>& req) {
        int m = req.size();
        ll ans = 1;
        vector<pair<int,int>> vc;
        for(int i=0; i<m; i++){
            vc.push_back({req[i][0], req[i][1]});
        }
        sort(vc.begin(), vc.end());

        int s = 0;
        int cnt = 0;

        // get(1,1,0);

        for(int i=0; i<m; i++){
            // cout << s << vc[i].first << vc[i].second-cnt << '\n';
            ans = ans*get(s+1, vc[i].first+1, vc[i].second-cnt);
            ans = ans %1000000007;
            // cout << ans << " ";
            s = vc[i].first+1;
            cnt = vc[i].second;
        }
        // cout << '\n';

        return ans;
    }
};
