#include <vector>
#include <unordered_map>
using namespace std;
//CODEINGSOLUTION :)
class Solution {
private:
    static const int MOD = 1'000'000'007;
    static const int MAX_INVERSIONS = 400;

public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        unordered_map<int, int> endIdxToInversionCnt;
        for (auto& req : requirements) {
            endIdxToInversionCnt[req[0] + 1] = req[1]; 
        }

        vector<vector<long long>> dp(n + 1, vector<long long>(MAX_INVERSIONS + 1, 0));
        dp[0][0] = 1;

        int length = 1;
        while (length <= n) {
            int currentInversions = 0;
            while (currentInversions <= MAX_INVERSIONS) {
                int newPosition = 0;
                while (newPosition < length) {
                    int previousInversions = currentInversions - newPosition;
                    if (previousInversions >= 0) {
                        dp[length][currentInversions] = (dp[length][currentInversions] + dp[length - 1][previousInversions]) % MOD;
                    }
                    ++newPosition;
                }
                ++currentInversions;
            }

            if (endIdxToInversionCnt.count(length) > 0) {
                int requiredInversions = endIdxToInversionCnt[length];
                int currentInversions = 0;
                while (currentInversions <= MAX_INVERSIONS) {
                    if (currentInversions != requiredInversions) {
                        dp[length][currentInversions] = 0;
                    }
                    ++currentInversions;
                }
            }

            ++length;
        }

        long long result = 0;
        int currentInversions = 0;
        while (currentInversions <= MAX_INVERSIONS) {
            result = (result + dp[n][currentInversions]) % MOD;
            ++currentInversions;
        }

        return static_cast<int>(result);
    }
};
