class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        int MOD = 1e9+7;
        unordered_map<int, int> mp;
        for (auto& req : requirements) mp[req[0] + 1] = req[1];
        vector<vector<long long>> dp(n + 1, vector<long long>(401, 0));
        dp[0][0] = 1;
        for (int i = 1; i <= n; i++) {
            for (int j = 0; j <= 400; j++)
                for (int q = 0; q < i; q++)
                    if (j - q >= 0) dp[i][j] = (dp[i][j] + dp[i - 1][j - q]) % MOD;
            if (mp.count(i) > 0) for (int k = 0; k <= 400; k++) if (k != mp[i]) dp[i][k] = 0;
        }
        long long ans = 0;
        for (int i = 0; i <= 400; i++) ans = (ans + dp[n][i]) % MOD;
        return ans;
    }
};
