class Solution:
    def numberOfPermutations(self, n: int, requirements: List[List[int]]) -> int:
        MOD = 10 ** 9 + 7
        MAX_INV = 405
        req_map = {req[0] + 1: req[1] for req in requirements}

        pc = [[0] * (MAX_INV + 1) for _ in range(n + 1)]
        pc[0][0] = 1

        for i in range(1, n + 1):
            for inv in range(MAX_INV + 1):
                for j in range(i):
                    prev_inv = inv - j
                    if prev_inv >= 0:
                        pc[i][inv] = (pc[i][inv] + pc[i - 1][prev_inv]) % MOD

            if i in req_map:
                target = req_map[i]
                for inv in range(MAX_INV + 1):
                    if inv != target:
                        pc[i][inv] = 0

        result = sum(pc[n]) % MOD

        return result
