class Solution {
    private static final int MOD = 1_000_000_007;
    private static final int MAX_INV = 400;

    public int numberOfPermutations(int n, int[][] requirements) {

        Map<Integer, Integer> req = new HashMap<>();
        for (int[] r : requirements) {
            req.put(r[0] + 1, r[1]);
        }

        long[][] perm = new long[n + 1][MAX_INV + 1];
        perm[0][0] = 1;

        for (int len = 1; len <= n; len++) {
            for (int inv = 0; inv <= MAX_INV; inv++) {
                for (int newPos = 0; newPos < len; newPos++) {
                    int prevInv = inv - newPos;
                    if (prevInv >= 0) {
                        perm[len][inv] = (perm[len][inv] + perm[len - 1][prevInv]) % MOD;
                    }
                }
            }

            if (req.containsKey(len)) {
                int targetInv = req.get(len);
                for (int inv = 0; inv <= MAX_INV; inv++) {
                    if (inv != targetInv) {
                        perm[len][inv] = 0;
                    }
                }
            }
        }

        long res = 0;
        for (long count : perm[n]) {
            res = (res + count) % MOD;
        }

        return (int) res;
    }
}
