class Solution {
    public int numberOfPermutations(int n, int[][] reqs) {

        final int MOD = 1_000_000_007;
        final int MAX_INV = 400;

        Map<Integer, Integer> r = new HashMap<>();
        for (int[] req : reqs) {
            r.put(req[0] + 1, req[1]);
        }

        long[][] pc = new long[n + 1][MAX_INV + 1];
        pc[0][0] = 1;

        for (int len = 1; len <= n; len++) {
            for (int inv = 0; inv <= MAX_INV; inv++) {
                for (int pos = 0; pos < len; pos++) {
                    int prevInv = inv - pos;
                    if (prevInv >= 0) {
                        pc[len][inv] = (pc[len][inv] + pc[len - 1][prevInv]) % MOD;
                    }
                }
            }

            if (r.containsKey(len)) {
                int targetInv = r.get(len);
                for (int inv = 0; inv <= MAX_INV; inv++) {
                    if (inv != targetInv) {
                        pc[len][inv] = 0;
                    }
                }
            }
        }

        long res = 0;
        for (long count : pc[n]) {
            res = (res + count) % MOD;
        }

        return (int) res;
    }

}
