const int MOD = 1e9 + 7;
int add(int x, int y) {
    return (x += y) >= MOD ? x - MOD : (x < 0 ? x + MOD : x);
}
int mult(int x, int y) {return 1ll * x * y % MOD;}
int dp[301][402];
int req[301], tar;
int rec(int id, int cnt) {
    if (cnt > 400 || (req[id] != -1 && req[id] != cnt)) {
        return 0;
    }
    if (dp[id][cnt] != -1) return dp[id][cnt];
    if (id == tar) return dp[id][cnt] = 1;
    dp[id][cnt] = 0;
    for (int i = 0; i <= id; ++i) {
        dp[id][cnt] = add(dp[id][cnt], rec(id + 1, cnt + i));
    }
    return dp[id][cnt];
}

class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        tar = n;
        memset(req, -1, sizeof req);
        for (auto &it: requirements) {
            req[it[0] +1] = it[1];
        }
        memset(dp, -1, sizeof dp);
        return rec(0, 0);        
    }
};
