class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        vector<int>rq(n+1,-1);
        for(auto v: requirements){
            rq[v[0]+1] = v[1];
        }
        vector<vector<int>>dp(n+1,vector<int>(401));
        const int mod = (int)1e9+7;
        if(rq[1] <= 0)dp[1][0] = 1;
        for(int i = 2; i<=n; i++){
            for(int e = 0; e<i; e++){
                for(int j = 0; j+e<=400; j++){
                    if(rq[i]!=-1 && j+e != rq[i])continue;
                    dp[i][j+e]+=dp[i-1][j];
                    dp[i][j+e]%=mod;
                }
            }
        }
        return dp[n][rq[n]];
    }
};
