#define ll long long
class Solution {
public:
    const ll int mod = 1e9+7;
    int numberOfPermutations(int n, vector<vector<int>>& v) {
        map<int,ll int> mp;
        for(auto x:v){
            mp[x[0]+1] = x[1];
        }
        vector<vector<ll int>> dp(n+1,vector<ll int>(401));
        dp[0][0] = 1;
        for(int i=1;i<=n;i++){
            for(int j=0;j<401;j++){
                for(int k=0;k<i;k++){
                    if(j-k >= 0){
                        dp[i][j] = (dp[i][j] + dp[i-1][j-k])%mod;
                    }
                }
            }
            if(mp.find(i)!=mp.end()){
                for(int j=0;j<401;j++){
                    if(mp[i] != j){
                        dp[i][j] = 0;
                    }
                }
            }
        }
        ll int ans = 0;
        for(auto x:dp[n]){
            ans = (ans + x)%mod;
        }
        return ans;
    }
};
