class Solution {
public:
    const int mod = 1000000007;
    int f(int n,vector<vector<int>>& requirements,int ind,int rind,int icnt,vector<vector<vector<int>>>& dp){
        if(ind>n) return 1;
        if(rind>=requirements.size()) return 1;
        if(icnt>requirements[rind][1]) return 0;
        if(dp[ind][rind][icnt]!=-1) return dp[ind][rind][icnt];
        if(requirements[rind][0]==ind){
            if(requirements[rind][1]==icnt) return f(n,requirements,ind+1,rind+1,icnt,dp)%mod; 
        }
        int ans = 0;
        for(int i=0;i<ind;i++){
            int cnt = icnt+ind-i;
            if(requirements[rind][0]==ind){
                if(requirements[rind][1]==cnt) ans = f(n,requirements,ind+1,rind+1,cnt,dp)%mod;
            }
            else{
                ans = (ans%mod + f(n,requirements,ind+1,rind, cnt,dp)%mod)%mod;
            }
        }

        if(requirements[rind][0]!=ind) 
        ans = (ans%mod+f(n,requirements,ind+1,rind,icnt,dp)%mod)%mod;
        // cout<<ind<<" "<<rind<<" "<<icnt<<" "<<ans<<endl;
        return dp[ind][rind][icnt] = ans;

    }
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        sort(requirements.begin(),requirements.end());
        int cind = 0;
        if(requirements[0][0]==0 && requirements[0][1]!=0) return 0;
        else if(requirements[0][0]==0 && requirements[0][1]==0) cind++;
        vector<vector<vector<int>>> dp(n,vector<vector<int>>(requirements.size(),vector<int>(401,-1)));
        return f(n,requirements,1,cind,0,dp);
        return 0;
    }
};
