int mod = 1e9 + 7;

int dp[310][410];

class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        vector<int> req(n, -1);
        for(auto it : requirements)
        {
            req[it[0]] = it[1];
        }

        function<int(int, int)> f = [&](int num, int inv) -> int
        {

             if(inv < 0)
             {
                return 0;
             }

             if(num == 0)
             {
                 return inv == 0;
             }

             if(req[num - 1] != -1 and req[num - 1] != inv)
             {
                 return 0;
             }

             int &ans = dp[num][inv]; if(ans != -1) return ans;

             ans = 0;
             for(int i = 1; i <= num; ++i)
             {
                 ans += f(num - 1, inv - (num - i));
                 ans %= mod;
             }

             return ans;
        };


        memset(dp, -1, sizeof dp);
        return f(n, req[n - 1]);
    }

};
