class Solution {
public:
    int mod=1e9+7;
    int dynamin[401][401];

    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        memset(dynamin,0,sizeof(dynamin));
        vector<int>ans(n,-1);
        dynamin[0][0]=1;
        for(auto i:requirements){
            ans[i[0]]=i[1];
        }
        for(int i=0;i<n;i++){
            for(int j=0;j<=400;j++){
                if(dynamin[i][j]!=0){
                    for(int k=0;k<=i;k++){
                        int m=j+k;
                        if(m>400)break;
                        dynamin[i+1][m]=(dynamin[i+1][m]+dynamin[i][j])%mod;
                    }
                }
            }
            if(ans[i]!=-1){
                for(int j=0;j<=400;j++){
                    if(j!=ans[i])dynamin[i+1][j]=0;
                }
            }
        }
        int res=0;
        for(int j=0;j<=400;j++)res=(res+dynamin[n][j])%mod;
        return res;
    }
};
