class Solution {
public:

    static bool cmp(vector<int>&v1,vector<int>&v2)
    {
        return v1[0]<v2[0];
    }
    int mod=1e9+7;

    vector<vector<long long>>dp;

    long long rec(int i,int cnt)
    {
        // cout<<i<<" "<<cnt<<endl;
        if(i==-1)
        {
            return cnt==0;
        }
        if(dp[i][cnt]!=-1ll)
        {
            return dp[i][cnt];
        }
        long long ans=0;
        for(int j=0;j<=i;j++)
        {
            if(j>cnt){continue;}
            ans=(ans+rec(i-1,cnt-j))%mod;
        }

        // cout<<"i"<<i<<" "<<cnt<<" "<<ans<<endl;

        return dp[i][cnt]=ans;
    }


    int numberOfPermutations(int n, vector<vector<int>>& requirements) {

        dp =vector<vector<long long>>(n,vector<long long>(401,-1ll));
        sort(requirements.begin(),requirements.end(),cmp);
        for(vector<int>&v:requirements)
        {
            for(int i=0;i<=400;i++)
            {
                dp[v[0]][i]=0ll;
            }
            dp[v[0]][v[1]]=-1ll;
        }

        int k=requirements.size()-1;

        return rec(requirements[k][0],requirements[k][1]);
    }
};
