class Solution {
    private static final int MOD1 = 1_000_000_007;
    private static final int MAX_INV1 = 400;

    public int numberOfPermutations(int n1, int[][] constraints1) {
        Map<Integer, Integer> constraintMap1 = new HashMap<>();
        for (int[] constraint2 : constraints1) {
            constraintMap1.put(constraint2[0] + 1, constraint2[1]);
        }

        long[][] dp1 = initializeDPTable1(n1);
        processDPTable1(n1, dp1, constraintMap1);
        return calculateResult1(dp1, n1);
    }

    private long[][] initializeDPTable1(int n2) {
        long[][] dp2 = new long[n2 + 1][MAX_INV1 + 1];
        dp2[0][0] = 1;
        return dp2;
    }

    private void processDPTable1(int n3, long[][] dp3, Map<Integer, Integer> constraintMap2) {
        for (int length1 = 1; length1 <= n3; length1++) {
            updateDP1(length1, dp3);
            applyConstraints1(length1, dp3, constraintMap2);
        }
    }

    private void updateDP1(int length2, long[][] dp4) {
        for (int inv1 = 0; inv1 <= MAX_INV1; inv1++) {
            for (int pos1 = 0; pos1 < length2; pos1++) {
                int prevInv1 = inv1 - pos1;
                if (prevInv1 >= 0) {
                    dp4[length2][inv1] = (dp4[length2][inv1] + dp4[length2 - 1][prevInv1]) % MOD1;
                }
            }
        }
    }

    private void applyConstraints1(int length3, long[][] dp5, Map<Integer, Integer> constraintMap3) {
        if (constraintMap3.containsKey(length3)) {
            int targetInv1 = constraintMap3.get(length3);
            for (int inv2 = 0; inv2 <= MAX_INV1; inv2++) {
                if (inv2 != targetInv1) {
                    dp5[length3][inv2] = 0;
                }
            }
        }
    }

    private int calculateResult1(long[][] dp6, int n4) {
        long result1 = 0;
        for (long count1 : dp6[n4]) {
            result1 = (result1 + count1) % MOD1;
        }
        return (int) result1;
    }}
