/**
 * @param {number} n
 * @param {number[][]} requirements
 * @return {number}
 */

var numberOfPermutations = function(n, reqs) {
    const M = 1000000007;
    const X = 400;

    let m = new Map();
    for (let [e, c] of reqs) {
        m.set(e + 1, c);
    }

    let d = Array(n + 1).fill().map(() => Array(X + 1).fill(0));
    d[0][0] = 1;

    for (let l = 1; l <= n; l++) {
        for (let i = 0; i <= X; i++) {
            for (let p = 0; p < l; p++) {
                let v = i - p;
                if (v >= 0) {
                    d[l][i] = (d[l][i] + d[l - 1][v]) % M;
                }
            }
        }

        if (m.has(l)) {
            let t = m.get(l);
            for (let i = 0; i <= X; i++) {
                if (i !== t) {
                    d[l][i] = 0;
                }
            }
        }
    }

    let r = 0;
    for (let c of d[n]) {
        r = (r + c) % M;
    }

    return r;
};
