const int MOD = 1e9 + 7;
const int MAX_INVERSIONS = 400;

class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        unordered_map<int, int> reqMap;
        for (const auto& req : requirements) {
            reqMap[req[0] + 1] = req[1];
        }

        vector<vector<long>> permCounts(n + 1, vector<long>(MAX_INVERSIONS + 1, 0));
        permCounts[0][0] = 1;

        for (int length = 1; length <= n; length++) {
            for (int inv = 0; inv <= MAX_INVERSIONS; inv++) {
                for (int newPos = 0; newPos < length; newPos++) {
                    int prevInv = inv - newPos;
                    if (prevInv >= 0) {
                        permCounts[length][inv] = (permCounts[length][inv] + permCounts[length - 1][prevInv]) % MOD;
                    }
                }
            }

            if (reqMap.find(length) != reqMap.end()) {
                int targetInv = reqMap[length];
                for (int inv = 0; inv <= MAX_INVERSIONS; inv++) {
                    if (inv != targetInv) {
                        permCounts[length][inv] = 0;
                    }
                }
            }
        }

        long result = 0;
        for (long count : permCounts[n]) {
            result = (result + count) % MOD;
        }

        return (int)result;
    }
};
