class Solution {
  public:
    int numberOfPermutations(int n, vector<vector<int>> &requirements) {
        memset(g, -1, sizeof g);
        for (auto req : requirements) {
            g[req[0]] = req[1];
        }
        f[0][0] = 1;

        for (int i = 0; i < n; i++) {
            if (g[i] != -1) {
                for (int j = 0; j <= 400; j++) {
                    if (j != g[i])
                        f[i][j] = 0;
                }
            }
            for (int j = 0; j <= i + 1; j++)
                for (int k = 0; k <= 400; k++)
                    if (f[i][k] > 0) {
                        if (j + k <= 400)
                            f[i + 1][j + k] = (f[i + 1][j + k] + f[i][k]) % mod;
                    }
        }
        return f[n - 1][g[n - 1]];
    }
    int f[310][510];
    int mod = 1e9 + 7;
    int g[310];
};
