package main

import (
	"fmt"
)

const (
	MOD           = 1_000_000_007
	MAX_INVERSIONS = 400
)

func numberOfPermutations(n int, requirements [][]int) int {
	reqMap := make(map[int]int)

	// Populate reqMap with requirements
	for _, req := range requirements {
		reqMap[req[0]+1] = req[1]
	}

	// Initialize 2D array for permutation counts
	permCounts := make([][]int, n+1)
	for i := range permCounts {
		permCounts[i] = make([]int, MAX_INVERSIONS+1)
	}

	permCounts[0][0] = 1

	// Fill permCounts using dynamic programming
	for length := 1; length <= n; length++ {
		for inv := 0; inv <= MAX_INVERSIONS; inv++ {
			for newPos := 0; newPos < length; newPos++ {
				prevInv := inv - newPos
				if prevInv >= 0 {
					permCounts[length][inv] = (permCounts[length][inv] + permCounts[length-1][prevInv]) % MOD
				}
			}
		}

		// Apply requirements constraints
		if targetInv, ok := reqMap[length]; ok {
			for inv := 0; inv <= MAX_INVERSIONS; inv++ {
				if inv != targetInv {
					permCounts[length][inv] = 0
				}
			}
		}
	}

	// Calculate final result
	result := 0
	for _, count := range permCounts[n] {
		result = (result + count) % MOD
	}

	return result
}
