class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        vector <vector<int>> dp(n+1, vector<int>(410, 0)), a; dp[0][0] = 1;
        int mod = 1e9+7;
        for(int i = 1; i<=n; i++){
            for(int j = 0; j<=400; j++){
                for(int k = 0; k<i; k++) if(j >= k) dp[i][j] = (dp[i][j] + dp[i-1][j-k]) % mod;
            }
            for(auto temp : requirements){
                int idx = temp[0]+1, cnt = temp[1];
                if(idx == i){
                    for(int j = 0; j<=400; j++){
                        if(j != cnt) dp[i][j] = 0;
                    }
                }
            }
        }
        int ans = 0; for(auto i : dp[n]) ans = (ans + i) % mod;
        return ans;
    }
};
