class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        int mod=1e9 + 7;
        int maxi=400;
        vector<vector<int>>t(n+1,vector<int>(maxi+1, 0));
        t[0][0] = 1;

        vector<int> req(n, -1);
        for (auto x : requirements) {
            req[x[0]]=x[1];
        }
        for (int h = 1;h <= n;++h) {
            for (int s=0;s<=400;++s) {
                for (int k = 0; k < h; ++k) {
                    if (s >= k) {
                        t[h][s] = (t[h][s] + t[h- 1][s - k])%mod;
                    }
                }
            }
            if (req[h - 1] != -1) {
                int lgaya = req[h - 1];
                for (int s = 0; s <=400; ++s) {
                    if (s != lgaya) {
                        t[h][s] = 0;
                    }
                }
            }
        }
        return t[n][req[n - 1]];

    }
};
