class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        sort(requirements.begin(), requirements.end());
        vector<long long> cnts(402, 0);
        int reqPos = 0;
        const long long MOD = 1e9+7;
        cnts[0] = 1;
        if(reqPos < requirements.size() && requirements[reqPos][0] == 0) {
            if(requirements[reqPos][1]!=0) return 0;
            reqPos++;
        }
        for(int i=1; i<n; i++){
            vector<long long> nextCnts(402, 0);
            for(int j=0; j<=400; j++){
                for(int k=0; j+k<=400 && k<=i; k++){
                    nextCnts[j+k] += cnts[j];
                    nextCnts[j+k] %= MOD;
                }
            }
            if(reqPos < requirements.size() && requirements[reqPos][0]==i){
                for(int j=0; j<=400; j++){
                    if(requirements[reqPos][1]!=j) nextCnts[j] = 0;
                }
                reqPos++;
            }
           //  for(int i=0; i<30; i++){
           //      int x = nextCnts[i];
           //      cout << x << ' '; 
           // }cout << '\n';
        cnts = nextCnts;
        }
        long long ans = 0;
        for(auto&x:cnts){
            ans += x;
            ans %= MOD;
        }
        return ans;
    }
};
