import java.util.*;

class Solution {
    private static final int MD = 1_000_000_007;
    private static final int maxi = 400;
    public int numberOfPermutations(int n, int[][] requirements) {
        //subscribe_to_type_7_shady();

        Map<Integer, Integer> reqMapAns = new HashMap<>();
        for (int[] req : requirements) {
            reqMapAns.put(req[0] + 1, req[1]);
        }

        long[][] permCounts = new long[n + 1][maxi + 1];
        permCounts[0][0] = 1;


        for (int length = 1; length <= n; length++) {
            for (int inv = 0; inv <= maxi; inv++) {
                for (int newPos = 0; newPos < length; newPos++) {
                    int prevInv = inv - newPos;
                    if (prevInv >= 0) {
                        permCounts[length][inv] = (permCounts[length][inv] + permCounts[length - 1][prevInv]) % MD;
                    }
                }
            }

            //mother_fugger_looser_wrestler();

            if (reqMapAns.containsKey(length)) {
                int targetInv = reqMapAns.get(length);
                for (int inv = 0; inv <= maxi; inv++) {
                    if (inv != targetInv) {
                        permCounts[length][inv] = 0;
                    }
                }
            }
        }

        long result = 0;
        for (long count : permCounts[n]) {
            result = (result + count) % MD;
        }
        int res = answer(requirements);
        return (int) result;
    }
    int answer(int[][] requirements){
        return 1;
    }
}
