#include <vector>
#include <unordered_map>
using namespace std;

class Solution {
public:
    static const int MOD = 1'000'000'007;
    static const int MAX_INVERSIONS = 400;

    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        // Map to store requirements where key is end index + 1 and value is inversion count
        unordered_map<int, int> reqMap;

        // Populate reqMap based on given requirements
        for (const auto& req : requirements) {
            reqMap[req[0] + 1] = req[1];
        }

        // DP table: dp[length][inv] will store number of permutations with 'length' elements and 'inv' inversions
        vector<vector<long long>> dp(n + 1, vector<long long>(MAX_INVERSIONS + 1, 0));

        // Base case: 1 way to arrange 0 elements with 0 inversions
        dp[0][0] = 1;

        // Fill the DP table
        for (int length = 1; length <= n; ++length) {
            for (int inv = 0; inv <= MAX_INVERSIONS; ++inv) {
                for (int newPos = 0; newPos < length; ++newPos) {
                    int prevInv = inv - newPos;
                    if (prevInv >= 0) {
                        dp[length][inv] = (dp[length][inv] + dp[length - 1][prevInv]) % MOD;
                    }
                }
            }

            // Apply requirements by setting non-matching inversions to 0
            if (reqMap.find(length) != reqMap.end()) {
                int targetInv = reqMap[length];
                for (int inv = 0; inv <= MAX_INVERSIONS; ++inv) {
                    if (inv != targetInv) {
                        dp[length][inv] = 0;
                    }
                }
            }
        }

        // Calculate the total number of valid permutations for 'n' elements
        long long result = 0;
        for (long long count : dp[n]) {
            result = (result + count) % MOD;
        }

        return static_cast<int>(result);
    }
};
