#define MOD 1000000007

class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        unordered_map<int,int>mpp;
        for(auto it:requirements){
            mpp[it[0]+1]=it[1];
        }
        vector<vector<long>>dp(n+1,vector<long>(400+1,0));
        dp[0][0]=1;
        for(int i=1;i<=n;i++){
            for(int j=0;j<=400;j++){
                for(int k=0;k<i;k++) {
                    int temp=j-k;
                    if(temp>=0) dp[i][j]=(dp[i][j]+dp[i-1][temp])%MOD;
                }
            }
            if(mpp.find(i)!=mpp.end()){
                int temp=mpp[i];
                for(int l=0;l<=400;l++) if(l!=temp) dp[i][l]=0;
            }
        }
        long ans=0;
        for(long it:dp[n]) ans=(ans+it)%MOD;
        return static_cast<int>(ans);
    }
};
